mapper.update:
update bot set
  title=#{title}, 
  cont=#{content}, 
  pt_path=#{photoPath},
  cpno=#{companyNo}
where botno=#{boycottNo}

update bot set
  view_cnt = view_cnt+1
where botno=#{boycottNo}

update bot set
  hodu_cnt = hodu_cnt+1
where botno=#{boycottNo}

update bot set
  bot_cnt = bot_cnt+1
where botno=#{boycottNo}

update bot set
  bot_cnt = bot_cnt-1
where botno=#{boycottNo}
